COMMENT ON TABLE person_discounts IS 'This table consists personal discounts for persons who made order in pizzeria.';
COMMENT ON COLUMN person_discounts.person_id IS 'This column consists id - row identificator.';
COMMENT ON COLUMN person_discounts.person_id IS 'This column consists person_id corresponding id from table person.';
COMMENT ON COLUMN person_discounts.pizzeria_id IS 'This column consists pizzeria_id corresponding id from table pizzeria.';
COMMENT ON COLUMN person_discounts.discount IS 'This column consists personal discount value for all persons.';